      <div className='modalContainer'>
        <button> X </button>
        <div className='helpPageContainer'>
          <div className='helpPage'>Are you sure you want to continue? </div>
        </div>

        <div className='helpPageContainer'>
          <div className='helpPage'>The next page is awesome!</div>
        </div>

        <button className='openModalBtn'>Open</button>

      </div>
    </div>


  )
}
