import { Board } from './components/Board';
import { Keyboard } from './components/Keyboard';
import './App.css';
import React, { useState, createContext, useEffect } from 'react';
import { boardDefault, generateWordSet } from './Words';
import  GameOver from './components/GameOver';
import { MdHelpOutline, MdSettings} from "react-icons/md";
import { IconContext } from 'react-icons/lib';
import help from './components/help'


export const AppContext = createContext();

function App() {
  const [board, setBoard] = useState(boardDefault);
  const [currAttempt, setCurrAttempt] = useState({ attempt:0, letterPos:0});
  const [wordSet, setWordSet] = useState(new Set());
  const [disabledLetters, setDisabledLetter] = useState([]);
  const [gameOver, setGameOver] = useState({gameOver:false,guessedWord:false});
  const [correctWord, setCorrectWord] = useState("");



  useEffect(() => {
    generateWordSet().then((words)=>{
      setWordSet(words.wordSet);
      setCorrectWord(words.todaysWord);
      console.log(words.todaysWord);
    }); 
  }, [])


  const onSelectLetter = (keyVal)=>{
    if(currAttempt.letterPos > 4) return;
    const newBoard = [...board];
    newBoard[currAttempt.attempt][currAttempt.letterPos] = keyVal;
    setBoard(newBoard);
    setCurrAttempt({...currAttempt, letterPos:currAttempt.letterPos+1});
  }

  const onDelete = ()=>{
    if(currAttempt.letterPos === 0) return;
    const newBoard = [...board];
    newBoard[currAttempt.attempt][currAttempt.letterPos - 1] = "";
    setBoard(newBoard);
    setCurrAttempt({...currAttempt, letterPos:currAttempt.letterPos-1});
  }

  const onEnter = ()=>{
    if(currAttempt.letterPos !== 5) return;
    let currWord = "";
    for(let i = 0; i < 5; i++){
      currWord += board[currAttempt.attempt][i];
    }
    if(wordSet.has(currWord.toLowerCase())){
      setCurrAttempt({attempt:currAttempt.attempt+1, letterPos: 0});
    }else{
      console.log(wordSet);
      alert("Word not Found");
    }
    if(currWord.toLowerCase() === correctWord.toLowerCase()){
      setGameOver({gameOver:true,guessedWord:true});
      return;
    }
    if(currAttempt.attempt >= 5){
      setGameOver({gameOver:true,guessedWord:false});
    } 

  }



  return (
    <div className="App">
      <IconContext.Provider value={{ color:"white", size:"2em"}}>
      <nav>
        <div className='header'>
        <div className='menu-left'>
          <button className='icon' >
            <MdHelpOutline onClick={()=>{handleClickHelp}}/>
          </button>
        </div>
        <h1>Wordle</h1>
        <div className="menu-right">
           <button className='icon'>
             <MdSettings onClick={()=>{alert("hello")}}/>
           </button>
        </div>
        </div>
      </nav>
      </IconContext.Provider>
      <AppContext.Provider value={{ board, setBoard, currAttempt, setCurrAttempt, onSelectLetter, onDelete, onEnter, correctWord, gameOver, setGameOver, disabledLetters, setDisabledLetter }}>
        <div className='game'>
        <Board />
        {gameOver.gameOver?<GameOver/>:<Keyboard />}
        </div>
      </AppContext.Provider>
    </div>
  );
}

export default App;
