import React, { useState } from 'react';
import '../App'
import { BrowserRouter as Router, Link, Routes, Route } from 'react-router-dom';
export default function Help() {


  return (
    <div className='modalBackground'>
      {/* <button>Close it</button> */}
      {/* // tutorial: how to create close button
      // https://www.youtube.com/watch?v=ZCvemsUfwPQ */}
      <div className='modalContainer'>
        <button onClick={()=>{
                <Router>
                <nav>
                <div className='header'>
                <Link to="/help" className='leftIcon'><MdHelpOutline/></Link>
                <Link to="/"><h1>Wordle</h1></Link>

                </div>
              </nav>
              <Routes>
                <Route path="/help" element={<Help/>}/>
              </Routes>
            
            </Router>
        } }> X </button>
        <div className='helpPageContainer'>
          <div className='helpPage'>Are you sure you want to continue? </div>
        </div>

        <div className='helpPageContainer'>
          <div className='helpPage'>The next page is awesome!</div>
        </div>

        <div className='helpPageContainer'>
          <button>Start Game</button>
        </div>

      </div>
    </div>


  )
}
