import React, {useContext} from "react";
import "./ToggleSwitch.css";
import { AppOverallContext } from '../AppOverall';

const ToggleSwitch3 = ({ label }) => {
    const { gameLevel, setGameLevel} = useContext(AppOverallContext);
    return (
        <div className="container">
            <input id="yes" type="radio" name="field" onClick={setGameLevel()} />
            <label for="no">Hard mode</label>

            <input id="no" type="radio" name="field" />
            <label for="off">Medium mode</label>

            <input type="radio" id="off" name="field" />
            <label for="yes">Easy mode</label>

        </div>
    );
};

export default ToggleSwitch3;
