import wordBank from './wordle-bank.txt';
import { AppContext } from '../App'
import React, { useContext } from 'react';



const { ROW, COL} = useContext(AppContext);
const rowIndex = Array.from(Array(ROW).keys());
const colIndex = Array.from(Array(COL).keys());

export const boardDefault = ()=>{ rowIndex.map(rowIdx => 
    colIndex.map((colIdx,rowIdx) => [{colIdx} ,{rowIdx}])
)};

// {rowIndex.map(rowIdx => 
//     <div className='row'>
//     {colIndex.map((colIdx,rowIdx) => <Letter letterPos={colIdx} attempVal={rowIdx}/>)}

export const generateWordSet = async()=>{
    let wordSet;
    let todaysWord;
    await fetch(wordBank).then((response)=> response.text()).then((result)=> {
        const wordArr = result.split("\r\n");
        todaysWord = wordArr[Math.floor(Math.random()*wordArr.length)]
        wordSet = new Set(wordArr);
});

return { wordSet, todaysWord };
};
