import React, { useContext } from "react";
import "./ToggleSwitch3.css";
import { AppOverallContext } from '../AppOverall';

const ToggleSwitch3 = () => {
    const { gameLevel, setGameLevel } = useContext(AppOverallContext);

    return (
        <div className="toggle">
                    Difficult mode:
                    <select onChange={(e)=>setGameLevel(e.target.value)}>
                        <option defaultValue="easy" >Select an Option</option>
                        <option value="easy">Easy</option>
                        <option value="medium">Medium</option>
                        <option value="hard">Hard</option>
                    </select>
                    <div className='footer'>
          <Link to="/" className="btn btn-primary"> Start Game </Link>
        </div>
                    
            
        </div>
    );
};

export default ToggleSwitch3;
